import { formatRelativeTime, formatFullDate, nowIso } from '@/lib/utils/format'

describe('Timezone S√£o Paulo', () => {
    it('formata datas no hor√°rio de SP', () => {
        // UTC 12:00 = SP 09:00 (UTC-3)
        const utcNoon = '2025-06-15T12:00:00.000Z'
        expect(formatFullDate(utcNoon)).toContain('09h00')
    })

    it('nowIso() retorna UTC (n√£o local)', () => {
        const iso = nowIso()
        expect(iso).toMatch(/Z$/) // deve terminar em Z (UTC)
    })
})

import { logger } from '@/lib/logger'
import { NextResponse } from 'next/server'
import { generateComment } from '@/lib/openrouter/generate'
import { getAuthenticatedUserId } from '@/lib/auth/user'
import { getActivePersona } from '@/db/queries/settings.queries'
import { getSettingsByUser } from '@/db/queries/settings.queries'
import { getStylesByUser } from '@/db/queries/styles.queries'
import { createComment } from '@/db/queries/comments.queries'
import { success, apiError } from '@/lib/utils/api-response'
import { db } from '@/db'
import { posts } from '@/db/schema/posts'
import { eq } from 'drizzle-orm'
import { checkRateLimit } from '@/lib/rate-limiter'
import { env } from '@/env'

export async function POST(req: Request) {
    try {
        const userId = await getAuthenticatedUserId()
        const { postId, postText, authorName, requestedStyleKey, authorHeadline } = await req.json()

        if (!postId || !postText || !requestedStyleKey) {
            return NextResponse.json(apiError('Faltam par√¢metros obrigat√≥rios', 400), { status: 400 })
        }

        // 1. Busca Post e Perfil para validar (e pegar o profileId que √© necess√°rio para Comments)
        const [dbPost] = await db.select().from(posts).where(eq(posts.id, postId)).limit(1)

        if (!dbPost) {
            return NextResponse.json(apiError('Post n√£o encontrado no banco', 404), { status: 404 })
        }

        // 2. Busca Settings e Persona
        const settings = await getSettingsByUser(userId)
        const persona = await getActivePersona(userId)
        const allStyles = await getStylesByUser(userId)

        const length = settings?.generationCount || 3

        const styleDef = allStyles.find(s => s.styleKey === requestedStyleKey)
        if (!styleDef) {
            return NextResponse.json(apiError('Estilo n√£o encontrado', 400), { status: 400 })
        }

        // 3. Verifica limites via mem√≥ria/Rate Limiter
        const rl = await checkRateLimit(`ai_${userId}`, env.AI_MAX_GENERATIONS_PER_MINUTE, 60_000)

        if (!rl.success) {
            return NextResponse.json(apiError('Limite de gera√ß√µes de IA atingido. Tente novamente em alguns instantes.', 429), {
                status: 429,
                headers: {
                    'X-RateLimit-Limit': rl.limit.toString(),
                    'X-RateLimit-Remaining': rl.remaining.toString(),
                    'X-RateLimit-Reset': rl.reset.toString(),
                }
            })
        }

        // 4. Chama a IA
        const response = await generateComment({
            postText,
            postAuthor: authorName,
            postAuthorRole: authorHeadline,
            styleId: requestedStyleKey as any,
            stylePrompt: styleDef.prompt,
            personaPrompt: persona?.compiledPrompt || '',
            count: length,
        })

        if (!response || !response.options) {
            return NextResponse.json(apiError('Erro na IA ou sem op√ß√µes geradas', 500), { status: 500 })
        }

        const optionsTexts = response.options.map((o) => o.text)

        // 5. Salva como Draft no banco de dados
        const newComment = await createComment({
            userId,
            postId,
            profileId: dbPost.profileId,
            text: optionsTexts[0] || '', // Texto principal inicial
            styleId: requestedStyleKey,
            source: 'ai',
            generatedOptions: optionsTexts,
            status: 'draft',
            postTextSnapshot: postText.substring(0, 150),
        })

        // Retorna as op√ß√µes e o ID do coment√°rio criado
        return NextResponse.json(success({
            commentId: newComment.id,
            options: optionsTexts,
        }))

    } catch (error) {
        logger.error({ err: error }, '[API] Erro ao gerar coment√°rio:')
        return NextResponse.json(apiError('Erro ao gerar coment√°rio', 500), { status: 500 })
    }
}

import { logger } from '@/lib/logger'
import { NextRequest } from 'next/server'
import { listAccounts } from '@/lib/unipile/accounts'
