interface ManualZoneProps {
    post: LinkedInPost
    onCancel: () => void
    onSuccess: () => void
}

export function ManualZone({ post, onCancel, onSuccess }: ManualZoneProps) {
    const [text, setText] = useState('')
    const postComment = usePostComment()
    const linkedinAccountId = useSettingsStore(s => s.linkedinAccountId)
    const addHistoryEntry = useHistoryStore(s => s.addEntry)
    const markAsPosted = useFeedStore(s => s.markAsPosted)

    const handlePost = async () => {
        if (!linkedinAccountId) {
            toast.error('Conta LinkedIn n√£o vinculada')
            return
        }

        try {
            const result = await postComment.mutateAsync({
                postId: post.linkedinPostId,
                text,
                accountId: linkedinAccountId,
            })

            if (result.id) {
                markAsPosted(post.id, {
                    text,
                    styleId: 'manual',
                    linkedinCommentId: result.id,
                    postedAt: new Date().toISOString(),
                    wasEdited: false
                })

                addHistoryEntry({
                    id: crypto.randomUUID(),
                    postId: post.id,
                    authorName: post.authorName,
                    authorColor: post.authorColor,
                    postSnippet: post.text.slice(0, 100),
                    text: text,
                    styleId: 'manual',
                    timestamp: new Date().toISOString(),
                    linkedinCommentId: result.id
                })

                toast.success('Coment√°rio enviado!')
                onSuccess()
            } else {
                toast.error('Erro ao postar coment√°rio')
            }
        } catch (err) {
            toast.error('Falha na conex√£o')
        }
    }

    return (
        <div className="p-4 border-t border-linkedflow-border bg-linkedflow-s1/80 animate-in fade-in slide-in-from-bottom-4 duration-300">
            <div className="flex items-center justify-between mb-4">
                <h4 className="text-[14px] font-bold text-linkedflow-text">
                    Coment√°rio Manual
                </h4>
                <button
                    onClick={onCancel}
                    className="p-1 hover:bg-linkedflow-s2 rounded-lg text-linkedflow-text3 transition-colors"
                >
                    <X size={18} />
                </button>
            </div>

            <div className="space-y-4">
                <div className="relative">
                    <Textarea
                        value={text}
                        onChange={(e) => setText(e.target.value)}
                        placeholder="O que voc√™ achou deste post?"
                        className="min-h-[120px] bg-linkedflow-s2 border-linkedflow-border focus:border-linkedflow-accent resize-none text-[14px] leading-relaxed rounded-xl p-4"
                        autoFocus
                    />
                    <div className="absolute bottom-3 right-3 text-[10px] text-linkedflow-text3 font-medium">
                        {text.length} caracteres
                    </div>
                </div>

                <div className="flex items-center justify-end gap-3">
                    <Button
                        variant="ghost"
                        onClick={onCancel}
                        className="text-linkedflow-text3 hover:text-linkedflow-text hover:bg-linkedflow-s2"
                    >
                        Cancelar
                    </Button>
                    <Button
                        onClick={handlePost}
                        disabled={postComment.isPending || !text.trim()}
                        className="bg-linkedflow-accent hover:bg-linkedflow-accent/90 text-white font-bold px-8 rounded-xl shadow-lg shadow-linkedflow-accent/10"
                    >
                        {postComment.isPending ? (
                            <>
                                <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                                Enviando...
                            </>
                        ) : (
                            <>
                                <SendHorizontal size={16} className="mr-2" />
                                Publicar
                            </>
                        )}
                    </Button>
                </div>
            </div>
        </div>
    )
