interface AddProfileModalProps {
    open?: boolean
    onOpenChange?: (open: boolean) => void
    inline?: boolean
}

const COLORS = ['#5b6ef5', '#22d3a0', '#f59e0b', '#f43f5e', '#a78bfa', '#ec4899', '#06b6d4']

export function AddProfileModal({ open, onOpenChange, inline = false }: AddProfileModalProps) {
    const [loading, setLoading] = useState(false)
    const [name, setName] = useState('')
    const [url, setUrl] = useState('')
    const [role, setRole] = useState('')

    const addProfile = useProfilesStore((s) => s.addProfile)
    const linkedinAccountId = useSettingsStore((s) => s.linkedinAccountId)
    const profiles = useProfilesStore((s) => s.profiles)

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault()

        if (!name || !url) {
            toast.error('Nome e URL s√£o obrigat√≥rios')
            return
        }

        setLoading(true)

        try {
            let linkedinId: string | undefined
            let finalName = name
            let finalRole = role
            let avatarUrl: string | undefined

            if (linkedinAccountId) {
                try {
                    const slug = url.split('/in/')[1]?.split('/')[0] || url
                    const res = await fetch(`/api/linkedin/profiles/${slug}?account_id=${linkedinAccountId}`)
                    const json = await res.json()

                    if (json.ok) {
                        linkedinId = json.data.id
                        avatarUrl = json.data.profile_picture_url
                        if (!name) finalName = json.data.name
                        if (!role) finalRole = json.data.headline || role
                    }
                } catch (err) {
                    console.warn('Falha ao buscar perfil no Unipile, usando dados manuais', err)
                }
            }

            const initials = finalName
                .split(' ')
                .map((n) => n[0])
                .join('')
                .slice(0, 2)
                .toUpperCase()

            const color = COLORS[profiles.length % COLORS.length]

            addProfile({
                id: crypto.randomUUID(),
                linkedinId,
                name: finalName,
                url,
                role: finalRole,
                avatarUrl,
                initials,
                color,
                active: true,
                addedAt: new Date().toISOString(),
            })

            toast.success(`${finalName} adicionado ao monitoramento`)
            if (onOpenChange) onOpenChange(false)
            setName('')
            setUrl('')
            setRole('')
        } catch (err) {
            toast.error('Erro ao adicionar perfil')
            console.error(err)
        } finally {
            setLoading(false)
        }
    }

    const formContent = (
        <form onSubmit={handleSubmit} className="grid gap-4 py-4 text-left">
            <div className="grid gap-2">
                <Label htmlFor="name" className="text-[13px] font-medium text-linkedflow-text2">
