'use client'

import { useEffect, useRef, useState } from 'react'
import { useQueryClient } from '@tanstack/react-query'
import { useSettingsStore } from '@/store/settings.store'

export function useAutoRefresh() {
    const qc = useQueryClient()
    const intervalMinutes = useSettingsStore(s => s.autoRefreshInterval || 0)
    const isEnabled = intervalMinutes > 0

    const [nextRefreshIn, setNextRefreshIn] = useState<number>(0)
    const timerRef = useRef<NodeJS.Timeout | null>(null)
    const countdownRef = useRef<NodeJS.Timeout | null>(null)

    useEffect(() => {
        // Limpa intervals anteriores
        if (timerRef.current) clearInterval(timerRef.current)
        if (countdownRef.current) clearInterval(countdownRef.current)

        if (!isEnabled) {
            setNextRefreshIn(0)
            return
        }

        const intervalMs = intervalMinutes * 60 * 1000
        setNextRefreshIn(intervalMinutes * 60)

        // Intervalo de Refresh real
        timerRef.current = setInterval(() => {
            qc.invalidateQueries({ queryKey: ['feed'] })
            setNextRefreshIn(intervalMinutes * 60)
        }, intervalMs)

        // Intervalo do Countdown visual
        countdownRef.current = setInterval(() => {
            setNextRefreshIn(prev => (prev > 0 ? prev - 1 : intervalMinutes * 60))
        }, 1000)

        return () => {
            if (timerRef.current) clearInterval(timerRef.current)
            if (countdownRef.current) clearInterval(countdownRef.current)
        }
    }, [intervalMinutes, isEnabled, qc])

    const formatTime = (seconds: number) => {
        const m = Math.floor(seconds / 60)
        const s = seconds % 60
        return `${m}:${s.toString().padStart(2, '0')}`
    }

    return {
        isEnabled,
        nextRefreshIn,
        formattedTime: formatTime(nextRefreshIn)
    }
}

import { useQuery } from '@tanstack/react-query'
import type { ApiResponse } from '@/lib/utils/api-response'

async function fetchFeed(): Promise<any[]> {
    const res = await fetch(`/api/linkedin/feed`)
    const json: ApiResponse<any[]> = await res.json()
    if (!json.ok) throw new Error(json.error?.message || 'Erro ao buscar feed')
    return json.data
}

export function useFeed() {
    return useQuery({
        queryKey: ['feed'],
        queryFn: fetchFeed,
        staleTime: 1000 * 60 * 5,    // 5 minutos
        refetchInterval: 1000 * 60 * 10, // refetch a cada 10 min
    })
}

export function getUnipileFriendlyError(errorObjectOrString: any): string {
    const errorMsg = String(errorObjectOrString).toLowerCase()
    if (!errorMsg) return 'Ocorreu um erro inesperado ao conectar ao LinkedIn.'

    if (errorMsg.includes('timeout') || errorMsg.includes('abort') || errorMsg.includes('tempo limite')) {
        return 'O LinkedIn (ou a API) demorou muito para responder. Tente novamente.'
    }
    if (errorMsg.includes('account disconnected') || errorMsg.includes('checkpoint') || errorMsg.includes('unauthorized')) {
        return 'Sua conta do LinkedIn foi desconectada por seguran√ßa (ou sess√£o expirada). Reautentique.'
    }
    if (errorMsg.includes('rate limit') || errorMsg.includes('429')) {
        return 'Limite de requisi√ß√µes do LinkedIn atingido. Tente mais tarde.'
    }
    if (errorMsg.includes('not found') || errorMsg.includes('404')) {
        return 'Recurso n√£o encontrado no LinkedIn. A postagem ou perfil pode n√£o ser p√∫blico ou foi removido.'
    }

    return 'Ocorreu um erro ao processar sua solicita√ß√£o no LinkedIn (API).'
}

import type { UnipilePost } from '@/types/unipile.types'
import type { LinkedInPost, PostMetrics } from '@/types/linkedin.types'
import type { MonitoredProfile } from '@/types/linkedin.types'
// Converte post da API Unipile para o modelo interno do app
export function mapUnipilePost(
    post: UnipilePost,
    profile: MonitoredProfile
): LinkedInPost {
    return {
        id: `local_${post.id}`,
        linkedinPostId: post.id,
        authorId: profile.id,
        authorName: profile.name,
        authorRole: profile.role,
        authorLinkedinId: post.author.id,
        authorAvatarUrl: post.author.profile_picture_url,
        authorInitials: profile.initials,
        authorColor: profile.color,
        text: post.text,
        imageUrls: post.media?.filter(m => m.type === 'IMAGE').map(m => m.url),
        videoUrl: post.media?.find(m => m.type === 'VIDEO')?.url,
        articleUrl: post.article?.url,
        articleTitle: post.article?.title,
        metrics: {
            likes: post.num_likes,
            comments: post.num_comments,
            reposts: post.num_reposts ?? 0,
            views: post.num_views,
        },
        postedAt: post.created_at,
        fetchedAt: new Date().toISOString(),
        commentStatus: 'idle',
        url: post.post_url,
    }
}

export function formatNumber(n: number): string {
    if (n >= 1000) return `${(n / 1000).toFixed(1)}k`
    return n.toString()
}

import { formatDistanceToNow, parseISO } from 'date-fns'
import { ptBR } from 'date-fns/locale'
import { toZonedTime, format as formatTz } from 'date-fns-tz'

const SP_TIMEZONE = 'America/Sao_Paulo'

// "h√° 2 horas", "h√° 3 dias" etc ‚Äî relativo ao hor√°rio de SP
export function formatRelativeTime(isoDate: string): string {
    const date = toZonedTime(parseISO(isoDate), SP_TIMEZONE)
    return formatDistanceToNow(date, { addSuffix: true, locale: ptBR })
}

// Data completa: "15 jan 2025, 14h30" no hor√°rio de SP
export function formatFullDate(isoDate: string): string {
    const date = toZonedTime(parseISO(isoDate), SP_TIMEZONE)
    return formatTz(date, "d MMM yyyy, HH'h'mm", { timeZone: SP_TIMEZONE, locale: ptBR })
}

// Apenas hora: "14:30"
export function formatTime(isoDate: string): string {
    const date = toZonedTime(parseISO(isoDate), SP_TIMEZONE)
    return formatTz(date, 'HH:mm', { timeZone: SP_TIMEZONE })
}

// Para gravar no banco: sempre UTC (ISO string)
export function nowIso(): string {
    return new Date().toISOString()
}
